#创建数据库
CREATE DATABASE `student_system`   

#选中数据库
USE student_system   

#创建学生表 字段（id、学号、姓名、班级编号、性别(1:男 2:女)、年龄、学院、创建时间）
CREATE TABLE IF NOT EXISTS `student` (
    `id` BIGINT (20) NOT NULL AUTO_INCREMENT COMMENT "id",
    `sno` BIGINT (20) NOT NULL COMMENT "学号",
    `stu_name` VARCHAR (10) NOT NULL COMMENT "姓名",
    `cno` BIGINT (20) COMMENT "班级编号",
    `sex` TINYINT (1) NOT NULL COMMENT "性别",
    `age` TINYINT (2) NOT NULL COMMENT "年龄",
    `school` VARCHAR (20) NOT NULL COMMENT "学院",
    `created_at` BIGINT (20) NOT NULL COMMENT "创建时间",
    `updated_at` BIGINT (20) NOT NULL COMMENT "修改时间",
    UNIQUE KEY `uk_sno` (`sno`),
    PRIMARY KEY (`id`)
) ENGINE = INNODB DEFAULT CHARSET = utf8  

 #创建班级表
 CREATE TABLE IF NOT EXISTS `class_info` (
  `id` BIGINT (20) NOT NULL AUTO_INCREMENT PRIMARY KEY COMMENT "id",
  `cno` BIGINT (20) NOT NULL COMMENT "班级编号",
  `c_name` VARCHAR (10) NOT NULL COMMENT "班级名称",
  `created_at` BIGINT (20) NOT NULL COMMENT "创建人id",
  `updated_at` BIGINT (20) NOT NULL COMMENT "修改人id",
  UNIQUE KEY `uk_cno` (cno)
) ENGINE = INNODB DEFAULT CHARSET = utf8 

#创建课程表
CREATE TABLE IF NOT EXISTS `course` (
    `id` BIGINT (20) NOT NULL AUTO_INCREMENT PRIMARY KEY COMMENT "id",
    `course_no` BIGINT (20) NOT NULL COMMENT "课程编号",
    `cno` BIGINT (20) NOT NULL COMMENT "班级编号",
    `course_name` VARCHAR (20) NOT NULL COMMENT "课程名",
    `course_score` DECIMAL (10) COMMENT "学分",
    `course_type` VARCHAR (10) NOT NULL COMMENT "课程性质",
    `teacher_name` VARCHAR (10) NOT NULL COMMENT "教师姓名",
    `created_at` BIGINT (20) NOT NULL COMMENT "创建时间",
    `updated_at` BIGINT (20) NOT NULL COMMENT "修改时间",
    UNIQUE KEY `uk_course_no` (`course_no`)
) ENGINE = INNODB AUTO_INCREMENT = 10000 DEFAULT CHARSET = utf8  

 #创建选课表
 CREATE TABLE IF NOT EXISTS `select_course`(
	`sno` BIGINT(20) NOT NULL COMMENT "学号",
	`course_no` BIGINT(20) NOT NULL COMMENT "课程号"
 )ENGINE=INNODB CHARSET=utf8 

#创建成绩表
CREATE TABLE IF NOT EXISTS `stu_score` (
    `id` BIGINT (20) NOT NULL AUTO_INCREMENT PRIMARY KEY COMMENT "id",
    `sno` BIGINT (20) NOT NULL COMMENT "学号",
    `course_no` BIGINT (20) NOT NULL COMMENT "课程编号",
    `score` DECIMAL (10) NOT NULL COMMENT "成绩",
    `created_at` BIGINT (20) NOT NULL COMMENT "创建时间",
    `updated_at` BIGINT (20) NOT NULL COMMENT "修改时间",
    UNIQUE KEY `uk_score` (`id`)
) ENGINE = INNODB DEFAULT CHARSET = utf8  

